        if emailTest.evaluate(with: string) {
            return ValidationResult.valid
        } else {
            return ValidationResult.invalid([Validator.Error.email])
        }
    }

    static func validatePasswordLength(_ string: String) -> ValidationResult {
        if string.characters.count > 5 && string.characters.count <= 25 {
            return ValidationResult.valid
        } else {
            return ValidationResult.invalid([Validator.Error.passwordLength])
        }
    }

    static func validatePasswordsMatch(_ passwordOne: String, _ passwordTwo: String) -> ValidationResult {
        if passwordOne == passwordTwo {
            return ValidationResult.valid
        } else {
            return ValidationResult.invalid([Validator.Error.passwordMismatch])
        }
    }

}
